// Create a sidebar menu in React
// The sidebar has a toggle button to expand and collapse the sidebar
// The sidebar has a menu with login and register buttons

import React from "react";
import './SidebarData.css'

export default class SidebarData extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            sidebarOpen: true
        }
    }


    toggleSidebar = () => {
        this.setState({
            sidebarOpen: !this.state.sidebarOpen
        })
    }

    // has a button that expands and collapses the sidebar
    // has a menu with login and register buttons
    // has an icon that 

    render() {
        return (
                <div className="sidebar py-5 my-5">
                <div className="toggle-btn" onClick={this.toggleSidebar}>


                <span></span>
                <span></span>
                <span></span>
                </div>
                <nav className={this.state.sidebarOpen ? "nav-menu active" : "nav-menu"}>
                <ul className="nav-menu-items">
                <li className="navbar-toggle">
                <div className="toggle-btn" onClick={this.toggleSidebar}>
                <span></span>
                <span></span>
                <span></span>
                </div>
                </li>
                <li className="nav-text">
                <a href="#">
                <i className="fa fa-user"></i>
                <span>Perfil</span>
                </a>
                </li>
                <li className="nav-text">
                <a href="#">
                <i className="fa fa-sign-in"></i>
                <span>Log In</span>
                </a>
                </li>
                <li className="nav-text">
                <a href="#">
                <i className="fa fa-user-plus"></i>
                <span>Register</span>
                </a>
                </li>
                </ul>
                </nav>
                </div>
        )
    }


    }
