import React from "react";

export default class JugadoresPagina extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            jugador: [],
            loading: true,
        }
    }

        // Fetch player id 44 from API fthesportdb.com
    // https://www.thesportsdb.com/api/v1/json/2/lookupplayer.php?id=34162040
    // obtain firstnames, lastname, dateOfBirth, position,

    componentDidMount() {
        fetch('https://api.football-data.org/v2/teams/86/matches?status=SCHEDULED', {
            method: "GET",
            headers: {
                "X-Auth-Token": "130849e0ce064febb45c9991f11c78a6"
            },
        })
            .then((res) => res.json())
            .then((data) => {
                this.setState({
                    persons: data.matches
                })
            })
            .catch((err) => console.log(err))
    }

    render() {
        return (
            <div className="container">
                <div className="row">
                    <div className="col">
                        <table className="table table-striped">
                            <thead>
                                <tr>
                                    <th scope="col">Nombre</th>
                                    <th scope="col">Apellido</th>
                                    <th scope="col">Fecha de nacimiento</th>
                                    <th scope="col">Posici√≥n</th>
                                </tr>
                            </thead>
                            <tbody>
                                {this.state.Persons.map((person) => (
                                    <tr>
                                        <td>{person.firstname}</td>
                                        <td>{person.lastname}</td>
                                        <td>{person.dateOfBirth}</td>
                                        <td>{person.position}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        )
    }
}
