import React from "react";

export default class JugadoresPagina extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            Jugadores: []
        }

    }
    componentDidMount() {
        fetch("http://api.football-data.org/v4/persons/44", {
            method: "GET",
            headers: {
                "X-Auth-Token": "130849e0ce064febb45c9991f11c78a6"
            },
        })
            .then((res) => res.json())
            .then((data) => {
                this.setState({
                    Jugadores: data.players
                })
            })
            .catch((err) => console.log(err))
    }

    render() {
        return (
            <div className="container">
                <div className="row">
                    <div className="col">
                        <table className="table table-striped">
                            <thead>
                                <tr>
                                    <th scope="col">Nombre</th>
                                    <th scope="col">Apellido</th>
                                    <th scope="col">Nacionalidad</th>
                                    <th scope="col">Posici√≥n</th>
                                    <th scope="col">Fecha de nacimiento</th>
                                </tr>
                            </thead>
                            <tbody>
                                {this.state.Jugadores.map((jugador) => (
                                    <tr>
                                        <td>{jugador.firstName}</td>
                                        <td>{jugador.lastName}</td>
                                        <td>{jugador.nationality}</td>
                                        <td>{jugador.position}</td>
                                        <td>{jugador.dateOfBirth}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        )
    }
}
