import React from "react";

export default class JugadoresPagina extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            players: []
        }

    }

    /* 
1. Fetch data from API https://www.thesportsdb.com/api/v1/json/2/lookupplayer.php?id=34162040
2. The result should be like this: https://www.thesportsdb.com/player/34162040
*/

    componentDidMount() {
        fetch("https://www.thesportsdb.com/api/v1/json/2/lookupplayer.php?id=34162040")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        Players: result
                    });
                }
            )
    }

    render() {
        return (
            <div>
                <h1>Jugadores</h1>
                <p>Nombre: {this.state.players.strPlayer}</p>
                <p>Fecha de Nacimiento: {this.state.Players.dateBorn}</p>
                <p>Posicion: {this.state.Players.strPosition}</p>
                <p>Deporte: {this.state.Players.strSport}</p>
                <p>Equipo: {this.state.Players.strTeam}</p>
                <p>Numero: {this.state.Players.strNumber}</p>
                <p>Descripcion: {this.state.Players.strDescriptionEN}</p>
                <p>Altura: {this.state.Players.strHeight}</p>
                <p>Peso: {this.state.Players.strWeight}</p>
                <p>Imagen: {this.state.Players.strThumb}</p>
            </div>
        )
    }
}
