 //add a toggle button to expand and collapse the sidebar



 // Path: artio/src/Sidebar.js

import React from 'react'
import './SidebarData.css'
import { Link } from 'react-router-dom'
import { SidebarData } from './SidebarData'

function Sidebar() {
    return (
        <div className="sidebar">
            <div className="sidebar__login">
                <button className="sidebar__loginButton">Login</button>
                <button className="sidebar__registerButton">Register</button>
            </div>
            <div className="sidebar__search">
                <input type="text" placeholder="Search.." name="search" />
                <button type="submit"><i className="fa fa-search"></i></button>
            </div>
            <div className="sidebar__menu">
                <ul className="sidebar__list">
                    {SidebarData.map((item, index) => {
                        return (
                            <li key={index} className={item.cName}>
                                <Link to={item.path}>
                                    {item.icon}
                                    <span>{item.title}</span>
                                </Link>
                            </li>
                        )
                    })}
                </ul>
            </div>
        </div>
    )
}

export default Sidebar
