      //FETCH DATA FROM API thesportsdb.com player id=34162040 https://www.thesportsdb.com/api/v1/json/2/lookupplayer.php?id=34162040

import react from "react";
import "../Jugadores.css";

export default class JugadoresPagina extends react.Component {
constructor(props) {
super(props);
this.state = {
Jugadores: []
};
}

componentDidMount() {
fetch("https://www.thesportsdb.com/api/v1/json/2/lookupplayer.php?id=34162040", {
method: "GET",
headers: {
"X-Auth-Token": "130849e0ce064febb45c9991f11c78a6"
},
})
.then((res) => res.json())
.then((data) => {
this.setState({
Jugadores: data.player
});
})
.catch((err) => console.log(err));
}

render() {
return (
<div className="container">
<div className="row">
<div className="col">
<section class="header-liga">
<div class="container justify-content-center align-items-center">
<div class="row justify-content-between">
<div class="col-lg-3 d-flex flex-row justify-content-center align-items-center">
<div class="header-left">
<p class="text"> Pr√≥ximos partidos </p>
</div>
</div>
<div class="col-lg-3 mx-5">
<div class="header">
<p class="text"> Mundial de Qatar 2022 </p>
</div>
</div>
</div>
</div>
</section>
<table className="table table-striped">
<thead>
<tr>
<th scope="col">Fecha</th>
<th scope="col">Local</th>
<th scope="col">Visitante</th>
</tr>
</thead>
<tbody>
{this.state.Jugadores.map((partido) => (
<tr>
<td>{partido.strPlayer}</td>
<td>{partido.strNationality}</td>
<td>{partido.strPosition}</td>
</tr>
))}
</tbody>
</table>
</div>
</div>
</div>
);
}
}

