// Todo a component that creates a sidebar menu on the left side of the screen.
// The sidebar menu has a login and register button.
// The sidebar menu has a list of links to other pages.
// the sidebar is expandable and collapsable.
// The sidebar menu has a search bar.



import React from "react";



export default class SidebarData extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            showMenu: false,
            showSidebar: false
        };
        this.showMenu = this.showMenu.bind(this);
        this.closeMenu = this.closeMenu.bind(this);
        this.showSidebar = this.showSidebar.bind(this);
        this.closeSidebar = this.closeSidebar.bind(this);
    }

    showMenu(event) {
        event.preventDefault();

        this.setState({ showMenu: true }, () => {
            document.addEventListener('click', this.closeMenu);
        });
    }

    closeMenu() {
        this.setState({ showMenu: false }, () => {
            document.removeEventListener('click', this.closeMenu);
        });
    }

    showSidebar(event) {
        event.preventDefault();

        this.setState({ showSidebar: true }, () => {
            document.addEventListener('click', this.closeSidebar);
        });
    }

    closeSidebar() {
        this.setState({ showSidebar: false }, () => {
            document.removeEventListener('click', this.closeSidebar);
        });
    }

    render() {
        return (
            <div className="row">
                <div className="col-12">
                    <div className="row">
                        <div className="col-1">
                            <button className="btn btn-primary" onClick={this.showSidebar}>Menu</button>
                            {
                                this.state.showSidebar
                                    ? (
                                        <div className="menu">
                                            <button className="btn btn-primary" onClick={this.showMenu}>Login</button>
                                            {
                                                this.state.showMenu
                                                    ? (
                                                        <div className="menu">
                                                            <button className="btn btn-primary">Register</button>
                                                        </div>
                                                    )
                                                    : (
                                                        null
                                                    )
                                            }
                                            <a href="https://www.google.com">Link 1</a>
                                            <a href="https://www.google.com">Link 2</a>
                                            <a href="https://www.google.com">Link 3</a>
                                            <a href="https://www.google.com">Link 4</a>
                                            <a href="https://www.google.com">Link 5</a>
                                            <a href="https://www.google.com">Link 6</a>
                                            <a href="https://www.google.com">Link 7</a>
                                            <a href="https://www.google.com">Link 8</a>
                                            <a href="https://www.google.com">Link 9</a>
                                            <a href="https://www.google.com">Link 10</a>
                                        </div>
                                    )
                                    : (
                                        null
                                    )
                            }
                        </div>
                    </div>
                </div>
            </div>
        );
    }


}
