import React from "react";

export default class JugadoresPagina extends React.Component {




    constructor(props) {
        super(props);
        this.state = {
            jugadores: []
        }
    }

    componentDidMount() {
        fetch("https://www.thesportsdb.com/api/v1/json/1/searchplayers.php?t=Barcelona")
            .then((response) => response.json())
            .then((data) => {
                this.setState({
                    jugadores: data.player
                })
            })
    }


    render() {
        return (
            <div className="container">
                <div className="row">
                    <div className="col">
                        <table className="table table-striped">
                            <thead>
                                <tr>
                                    <th scope="col">Nombre</th>
                                    <th scope="col">Posicion</th>
                                    <th scope="col">Numero</th>
                                </tr>
                            </thead>
                            <tbody>
                                {this.state.jugadores.map((jugadores) => (
                                    <tr>
                                        <td>{jugadores.strPlayer}</td>
                                        <td>{jugadores.strPosition}</td>
                                        <td>{jugadores.strNumber}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        );
    }
}
