// Create a sidebar menu in React
// The sidebar has a toggle button to expand and collapse the sidebar
// The state will change when the toggle button is clicked
// The sidebar has a menu with login and register buttons

import React from "react";
import './SidebarData.css'

export default class SidebarData extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            sidebarOpen: true
        }
    }


    showSidebar = () => this.setState({ sidebar: !this.state.sidebar });

    toggleSidebar = () => {
        this.setState({
            sidebarOpen: !this.state.sidebarOpen
        })
    }

        // has a menu with login and register buttons
    // has an icon that expands and collapses the sidebar
    render() {
        return (
            <div className="sidebar my-5 x">
                <a href="javascript:void(0);" className="icon" onClick={this.showSidebar}>
            &#9776; </a>
                <div className="toggle-button" onClick={this.toggleSidebar}>
                    <div className="bar bar1"></div>
                    <div className="bar bar2"></div>
                    <div className="bar bar3"></div>
                </div>

                <div className={this.state.sidebarOpen ? "sidebar-menu open" : "sidebar-menu"}>
                    <div
          id="mySidebar"
          className="sidebar"
          style={
            this.state.sidebar
              ? { display: "block" }
              : { display: "none" }
          }
        ></div><a href="javascript:void(0)" className="closebtn" onClick={this.showSidebar}>
            &times;
          </a>
                    <ul>
                        <li>
                            <a href="#">Login</a>
                        </li>
                        <li>
                            <a href="#">Register</a>
                        </li>
                    </ul>
                </div>
            </div>
        )
    }
}
