import React from "react";

export default class JugadoresPagina extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
           Persons: []
        }

    }

     // Fetch player id 44 from API football-data.org
    // https://api.football-data.org/v4/persons/44
    // obtain firstnames, lastname, dateOfBirth, position, 

    // Fetch player id 44 from API football-data.org
    // https://api.football-data.org/v4/persons/44
    // obtain firstnames, lastname, dateOfBirth, position,

    componentDidMount() {
        fetch('https://api.football-data.org/v4/persons/44', {
            method: "GET",
            headers: {
                "X-Auth-Token": "130849e0ce064febb45c9991f11c78a6"
            },
        })
            .then((res) => res.json())
            .then((data) => {
                this.setState({
                    Persons: data
                })
            })
            .catch((err) => console.log(err))
    }

    render() {
        return (
            <div className="container">
                <div className="row">
                    <div className="col">
                        <table className="table table-striped">
                            <thead>
                                <tr>
                                    <th scope="col">Nombre</th>
                                    <th scope="col">Apellido</th>
                                    <th scope="col">Fecha de nacimiento</th>
                                    <th scope="col">Posici√≥n</th>
                                </tr>
                            </thead>
                            <tbody>
                                <tr>
                                    <td>{this.state.Persons.firstnames}</td>
                                    <td>{this.state.Persons.lastname}</td>
                                    <td>{this.state.Persons.dateOfBirth}</td>
                                    <td>{this.state.Persons.position}</td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        );
    }
}
