import React from "react";

export default class JugadoresPagina extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            Jugadore: []
        }

    }
    componentDidMount() {
        fetch("https://www.thesportsdb.com/api/v1/json/2/searchplayers.php?p=Diego%20Maradona",
                               {
                method: "GET",
                headers: {
                    'Content-Type': 'application/json'
                }
            }
        )
            .then(response => response.json())
            .then(response => {
                this.setState({
                    Mundial: response.player
                })
            })
    }


    render() {
        return (

            <div className="container">
                <div className="row">
                    <div className="col">
                        <table className="table table-striped">
                            <thead>
                                <tr>
                                    <th scope="col">Nombre</th>
                                    <th scope="col">Fecha de nacimiento</th>
                                    <th scope="col">Pa√≠s</th>
                                </tr>
                            </thead>
                            <tbody>
                                {this.state.Mundial.map((jugador) => (
                                    <tr>
                                        <td>{jugador.strPlayer}</td>
                                        <td>{jugador.dateBorn}</td>
                                        <td>{jugador.strNationality}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        )
    }

}
