import React from "react";





export default class JugadoresPagina extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            jugadores: [],
            nombre: '',
            apellido: '',
            fecha_nacimiento: '',
            nacionalidad: '',
            posicion: '',
            altura: '',
            peso: '',
            club: '',
            liga: '',
            torneo: '',
            nombreClub: '',
            nombreLiga: '',
            nombreTorneo: '',


        }





    }


    // import React from "react";
    // constructor (props) {

    // Fetch data from API https://www.thesportsdb.com/api/v1/json/2/lookupplayer.php?id=34162040
    // strPlayer, dateborn, strNationality, strDescriptionEN, strPosition, strHeight, strWeight, strThumb, strCutout, strFanart1, strFanart2, strFanart3, strFanart4, strBanner, strTeam, strTeamBadge, strTeamJersey, strTeamLogo, strTeamFanart1, strTeamFanart2, strTeamFanart3, strTeamFanart4, strLocked
    // Render data in a table

    async componentDidMount() {
        const url = "https://www.thesportsdb.com/api/v1/json/2/lookupplayer.php?id=34162040";
        const response = await fetch(url);
        const data = await response.json();
        this.setState({ jugadores: data.player[0] });
        this.setState({ nombre: data.player[0].strPlayer });
        this.setState({ apellido: data.player[0].strPlayer });
        this.setState({ fecha_nacimiento: data.player[0].dateBorn });
        this.setState({ nacionalidad: data.player[0].strNationality });
        this.setState({ posicion: data.player[0].strPosition });
        this.setState({ altura: data.player[0].strHeight });
        this.setState({ peso: data.player[0].strWeight });
        this.setState({ club: data.player[0].strTeam });
        this.setState({ liga: data.player[0].strLeague });
        this.setState({ torneo: data.player[0].strTournament });
        this.setState({ nombreClub: data.player[0].strTeam });
        this.setState({ nombreLiga: data.player[0].strLeague });
        this.setState({ nombreTorneo: data.player[0].strTournament });

    }

    render() {
        return (
            <div>
                <div className="row">
                    <div className="col-12 col-md-4">
                        <img src={this.state.jugadores.strCutout} alt={this.state.nombre} className="w-100" />
                    </div>
                    <div className="col-12 col-md-8">
                        <h1>{this.state.nombre}</h1>
                        <p>{this.state.jugadores.strDescriptionEN}</p>
                        <p>Fecha de nacimiento: {this.state.fecha_nacimiento}</p>
                        <p>Nacionalidad: {this.state.nacionalidad}</p>
                        <p>Altura: {this.state.altura}</p>
                        <p>Peso: {this.state.peso}</p>
                        <p>Club: {this.state.nombreClub}</p>
                        <p>Liga: {this.state.nombreLiga}</p>
                        <p>Torneo: {this.state.nombreTorneo}</p>

                    </div>
                </div>
            </div>
        );
    }
}
