// Create a sidebar menu in React
// The sidebar has a toggle button to expand and collapse the sidebar
// the sidebar is responsive and will collapse on smaller screens
// The state will change when the toggle button is clicked
// The sidebar has a menu with login and register buttons


import React from "react";
import "./SidebarData.css";

export default class Sidebar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isToggleOn: false
    };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    this.setState(state => ({
      isToggleOn: !state.isToggleOn
    }));
  }


  render() {
    return (
      <div>
        <button className="toggle-button" onClick={this.handleClick}>
          &#9776;
        </button>
        <nav className={this.state.isToggleOn ? "nav-menu active" : "nav-menu"}>
          <ul>
            <li>
              <a href="/">Login</a>
            </li>
            <li>
              <a href="/">Register</a>
            </li>
          </ul>
        </nav>
      </div>
    );
  }
}
