// Todo a component that creates a sidebar menu on the left side of the screen.
// The sidebar menu has a login and register button.
// The sidebar menu has a list of links to other pages.
// the sidebar is expandable and collapsable.
// The sidebar menu has a search bar.
// the sidebar menu is located at left side of the screen.



import React from "react";

export default class SidebarData extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      sidebar: false,
    };
  }

  // The sidebar menu is located at left side of the screen.
  // The sidebar menu has a login and register button.
  // The sidebar menu has a list of links to other pages.
  // the sidebar is expandable and collapsable.
  // The sidebar menu has a search bar.

  render() {
    return (
      <div>
        <div className="sidebar">
          <div className="sidebar__title">
            <div className="sidebar__img">
              <img
                src="https://image.flaticon.com/icons/png/512/25/25231.png"
                alt="logo"
              />
              <h1>Artio</h1>
            </div>
            <i
              onClick={() => this.setState({ sidebar: !this.state.sidebar })}
              className="fa fa-times"
              id="sidebarIcon"
              aria-hidden="true"
            ></i>
          </div>
          <div className="sidebar__menu">
            <div className="sidebar__link active_menu_link">
              <i className="fa fa-home"></i>
              <a href="#">Dashboard</a>
            </div>
            <h2>MNG</h2>
            <div className="sidebar__link">
              <i className="fa fa-user-secret" aria-hidden="true"></i>
              <a href="#">Admin</a>
            </div>
            <div className="sidebar__link">
              <i className="fa fa-building" aria-hidden="true"></i>
              <a href="#">Branch</a>
            </div>
            <div className="sidebar__link">
              <i className="fa fa-users" aria-hidden="true"></i>
              <a href="#">Employee</a>
            </div>
            <div className="sidebar__link">
              <i className="fa fa-archive" aria-hidden="true"></i>
              <a href="#">Product</a>
            </div>
            <div className="sidebar__link">
              <i className="fa fa-shopping-cart" aria-hidden="true"></i>
              <a href="#">Order</a>
            </div>
            <div className="sidebar__link">
              <i className="fa fa-money" aria-hidden="true"></i>
              <a href="#">Sales</a>
            </div>
            <div className="sidebar__link">
              <i className="fa fa-bar-chart" aria-hidden="true"></i>
              <a href="#">Reports</a>
            </div>
            <div className="sidebar__link">
              <i className="fa fa-cog" aria-hidden="true"></i>
              <a href="#">Settings</a>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
