import React from "react";

export default class JugadoresPagina extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            Jugadores: []
        }

    }
    componentDidMount() {
      //FETCH DATA FROM AN OPENSOURCE FOOTBALL API thesportsdb.com player 34162040
        fetch ("https://www.thesportsdb.com/api/v1/json/1/searchplayers.php?p=34162040")
        .then(response => response.json())
        .then(data => {
            this.setState({
                Jugadores: data.player
            })
        })
    }




    render() {
        return (
            <div className="container">
                <div className="row">
                    <div className="col">
                       <section class="header-liga">
        <div class="container justify-content-center align-items-center">
            <div class="row justify-content-between">
                <div class="col-lg-3 d-flex flex-row justify-content-center align-items-center">
                    <div class="header-left">
                        <p class="text"> Pr√≥ximos partidos </p>
                    </div>
                </div>
                <div class="col-lg-3 mx-5">
                    <div class="header">
                        <p class="text"> Mundial de Qatar 2022 </p>
                    </div>
                </div>
            </div>
        </div>
    </section>
                        <table className="table table-striped">
                            <thead>
                                <tr>
                                    <th scope="col">Fecha</th>
                                    <th scope="col">Local</th>
                                    <th scope="col">Visitante</th>
                                </tr>
                            </thead>
                            <tbody>
                                {this.state.Mundial.map((partido) => (
                                    <tr>
                                        <td>{partido.utcDate}</td>
                                        <td>{partido.homeTeam.name}</td>
                                        <td>{partido.awayTeam.name}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        )   
}
}
