import React from "react";

import './SidebarData.css'

export default class SidebarData extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            sidebarOpen: true
        }
    }


    showSidebar = () => this.setState({ sidebar: !this.state.sidebar });




    render() {
        return (
            <div className="sidebar">
                <button onClick={this.showSidebar}>
                    &#9776;
                </button>
                <nav className={this.state.sidebar ? 'sidebar-menu active' : 'sidebar-menu'} >
                    <ul className="sidebar-menu-items" onClick={this.showSidebar}>
                        <li className="sidebar-toggle">
                            <a href="#">X</a>
                        </li>
                        <li className="sidebar-menu-item">
                            <a href="#">Inicio</a>
                        </li>
                        <li className="sidebar-menu-item">
                            <a href="#">Torneos</a>
                        </li>
                        <li className="sidebar-menu-item">
                            <a href="#">Ligas</a>
                        </li>
                        <li className="sidebar-menu-item">
                            <a href="#">Contacto</a>
                        </li>
                        <li className="sidebar-menu-item">
                            <a href="#">Login</a>
                        </li>
                    </ul>
                </nav>
            </div>
        )
    }
}
