// Todo a component that creates a sidebar menu on the left side of the screen.
// The sidebar menu has a login and register button.
// The sidebar menu has a list of links to other pages.
// the sidebar is expandable and collapsable.
// The sidebar menu has a search bar.



import React from "react";



export default class SidebarData extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            sidebar: [
                {
                    title: "Home",
                    path: "/",
                    cName: "nav-text"
                },
                {
                    title: "Services",
                    path: "/services",
                    cName: "nav-text"
                },
                {
                    title: "Products",
                    path: "/products",
                    cName: "nav-text"
                },
                {
                    title: "Contact Us",
                    path: "/contact-us",
                    cName: "nav-text"
                },
                {
                    title: "Sign Up",
                    path: "/sign-up",
                    cName: "nav-text"
                }
            ]
        }
    }



}
