// Create a sidebar menu in React
// The sidebar has a toggle button to expand and collapse the sidebar
// The state will change when the toggle button is clicked
// The sidebar has a menu with login and register buttons

import React from "react";



class SidebarData extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      sidebar: false
    };
  }

  showSidebar = () => this.setState({ sidebar: !this.state.sidebar });

  render() {
    return (
      <div>
        <div className="navbar">
          <div className="menu-bars">
            <i className="fas fa-bars" onClick={this.showSidebar} />
          </div>
          <nav className={this.state.sidebar ? "nav-menu active" : "nav-menu"}>
            <ul className="nav-menu-items">
              <li className="navbar-toggle">
                <div className="menu-bars">
                  <i className="fas fa-bars" onClick={this.showSidebar} />
                </div>
              </li>
              {SidebarData.map((item, index) => {
                return (
                  <li key={index} className={item.cName}>
                    <Link to={item.path}>
                      {item.icon}
                      <span>{item.title}</span>
                    </Link>
                  </li>
                );
              })}
            </ul>
          </nav>
        </div>
      </div>
    );
  }
}

export default SidebarData;
