import React from "react";

export default class JugadoresPagina extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            jugadores: [],
            nombre: "",
            apellido: "",
            edad: "",
            posicion: "",
            club: "",
            liga: "",
            nacionalidad: "",
            img: "",
            descripcion: "",
            parrafo: "",
            loading: true,
            error: null,
            form: {
                nombre: "",
                apellido: "",
                edad: "",
                posicion: "",
                club: "",
                liga: "",
                nacionalidad: "",
                img: "",
                descripcion: "",
                parrafo: "",
            }
        };
    }

     // Fetch player id 44 from API football-data.org
    // https://api.football-data.org/v4/persons/44
    // obtain firstnames, lastname, dateOfBirth, position, 

    // Fetch player id 44 from API football-data.org
    // https://api.football-data.org/v4/persons/44
    // obtain firstnames, lastname, dateOfBirth, position,

    componentDidMount() {
        fetch('https://api.football-data.org/v4/persons/44', {
            method: "GET",
            headers: {
                "X-Auth-Token": "130849e0ce064febb45c9991f11c78a6"
            },
        })
            .then((res) => res.json())
            .then((data) => {
                this.setState({
                    Persons: data
                })
            })
            .catch((err) => console.log(err))
    }

    render() {
        return (
            <div className="container">
                <div className="row">
                    <div className="col">
                        <table className="table table-striped">
                            <thead>
                                <tr>
                                    <th scope="col">Nombre</th>
                                    <th scope="col">Apellido</th>
                                    <th scope="col">Edad</th>
                                    <th scope="col">Posición</th>
                                    <th scope="col">Club</th>
                                    <th scope="col">Liga</th>
                                    <th scope="col">Nacionalidad</th>
                                    <th scope="col">Imagen</th>
                                    <th scope="col">Descripción</th>
                                    <th scope="col">Parrafo</th>
                                </tr>
                            </thead>
                            <tbody>
                                {this.state.Persons.map((jugador) => (
                                    <tr key={jugador.id}>
                                        <td>{jugador.name}</td>
                                        <td>{jugador.lastName}</td>
                                        <td>{jugador.dateOfBirth}</td>
                                         <td>{jugador.position}</td>

                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        );
    }
}
