import React from "react";
import { Card, Button } from "react-bootstrap";

export default class JugadoresPagina extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      jugador: [],
    };
  }

  componentDidMount() {
    fetch("https://www.thesportsdb.com/api/v1/json/2/lookupplayer.php?id=34162040")
      .then((res) => res.json())
      .then((data) => {
        this.setState({
          jugador: data.player,
        });
      })
      .catch((err) => console.log(err));
  }

  render() {
    return (
      <div className="container">
        <div className="row">
          <div className="col">
            <Card style={{ width: "18rem" }}>
              <Card.Img variant="top" src={this.state.jugador.strThumb} />
              <Card.Body>
                <Card.Title>{this.state.jugador.strPlayer}</Card.Title>
                <Card.Text>{this.state.jugador.strDescriptionEN}</Card.Text>
                <Button variant="primary">Go somewhere</Button>
              </Card.Body>
            </Card>
          </div>
        </div>
      </div>
    );
  }
}
