import React from "react";

// Todo a component that creates a sidebar menu on the left side of the screen.
// The sidebar menu has a login and register button.
// The sidebar menu has a list of links to other pages.

export default class SidebarData extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            sidebar: [
                {
                    title: "Home",
                    path: "/",
                    cName: "nav-text"
                },
                {
                    title: "Services",
                    path: "/services",
                    cName: "nav-text"
                },
                {
                    title: "Products",
                    path: "/products",
                    cName: "nav-text"
                },
                {
                    title: "Contact Us",
                    path: "/contact-us",
                    cName: "nav-text"
                },
                {
                    title: "Sign Up",
                    path: "/sign-up",
                    cName: "nav-text"
                }
            ]
        }
    }

    render() {
        return (
            <nav className={this.props.sidebar ? 'nav-menu active' : 'nav-menu'}>
                <ul className="nav-menu-items" onClick={this.props.showSidebar}>
                    <li className="navbar-toggle">
                        <a href="#" className="menu-bars">
                            <i className="fas fa-times"></i>
                        </a>
                    </li>
                    {this.state.sidebar.map((item, index) => {
                        return (
                            <li key={index} className={item.cName}>
                                <a href={item.path}>
                                    {item.title}
                                </a>
                            </li>
                        )
                    })}
                </ul>
            </nav>
        )
    }
}

