import React from "react";
import './Equipos.css'
import './index';
class Container extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      mode: "light"
    };

    this.handleLightMode = this.handleLightMode.bind(this);
    this.handleDarkMode = this.handleDarkMode.bind(this);
  }
  handleLightMode() {
    this.setState({
      mode: "light"
    });
  }
  handleDarkMode() {
    this.setState({
      mode: "dark"
    });
  }
  render() {
    const { mode } = this.state;

    return (
      <div
        style={{
          height: "100%",
          background: mode === "light" ? "#fff" : "#333333"
        }}
      >
        {mode === "light" ? (
          <button onClick={this.handleDarkMode}>Dark Mode</button>
        ) : (
          <button onClick={this.handleLightMode}>Light Mode</button>
        )}
      </div>
    );
  }
}

ReactDOM.render(<Container />, document.getElementById("darkmode"));
