// Create a sidebar menu in React
// The sidebar has a toggle button to expand and collapse the sidebar
// The sidebar has a menu with login and register buttons

import React from "react";
import './Sidebar.css'

export default class SidebarData extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            sidebarOpen: true
        }
    }

    toggleSidebar = () => {
        this.setState({
            sidebarOpen: !this.state.sidebarOpen
        })
    }

    render() {
        return (
            <div className="sidebar">
                <div className="sidebar-toggle" onClick={this.toggleSidebar}>
                    <div className="sidebar-toggle-line" />
                    <div className="sidebar-toggle-line" />
                    <div className="sidebar-toggle-line" />
                </div>
                <div className="sidebar-menu">
                    <div className="sidebar-menu-item">
                        <a href="/login">Login</a>
                    </div>
                    <div className="sidebar-menu-item">
                        <a href="/register">Register</a>
                    </div>
                </div>
            </div>
        )
    }
}
